package com.example.heart_to_heart.presentation.screen.main.follow.follower

import android.os.Bundle
import android.util.Log
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ImageView
import android.widget.TextView
import androidx.databinding.DataBindingUtil
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide
import org.koin.androidx.viewmodel.ext.android.viewModel
import com.example.heart_to_heart.R
import com.example.heart_to_heart.databinding.FragmentFollowerBinding
import com.example.heart_to_heart.presentation.screen.main.base.BaseMainFragment

class FollowerFragment : BaseMainFragment() {

    private val viewModel: FollowerViewModel by viewModel()
    private lateinit var binding: FragmentFollowerBinding

    private lateinit var recyclerView: RecyclerView
    private lateinit var recyclerViewAdapter: FollowerRecyclerViewAdapter

    val users = listOf(
        User("sonny", "data:image/jpeg;base64,"),
        User("Rachel", "https://s-media-cache-ak0.pinimg.com/736x/c1/e5/c1/c1e5c1ee11ebcaf5ce093b1c08304614.jpg"),
        User("Monica", "https://i.pinimg.com/originals/dd/bd/43/ddbd43396b44aa648dc7619f0111a163.jpg"),
        User("obama", "https://www.biography.com/.image/t_share/MTE4MDAzNDEwNzg5ODI4MTEw/barack-obama-12782369-1-402.jpg"),
        User("kroos", "https://upload.wikimedia.org/wikipedia/commons/3/36/FIFA_WC-qualification_2014_-_Austria_vs._Germany_2012-09-11_-_Toni_Kroos.JPG")
    )

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        Log.d("YOLO", "FollowerFragment: onCreate()")
    }

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        Log.d("YOLO", "FollowerFragment: onCreateView()")
        binding = DataBindingUtil.inflate(inflater, R.layout.fragment_follower, container, false)
        val rootView = binding.root
        recyclerView = rootView.findViewById(R.id.fragment_follower_rv)
        return rootView
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        viewModel.test()
        initUI()
        initBinding()
    }

    override fun onDestroyView() {
        Log.d("YOLO", "FollowerFragment: onDestroyView()")
        super.onDestroyView()
    }

    override fun onDestroy() {
        Log.d("YOLO", "FollowerFragment: onDestroy()")
        super.onDestroy()
    }

    private fun initUI() {
        initRecyclerView()
    }

    private fun initBinding() {

    }

    private fun initRecyclerView() {
        recyclerViewAdapter = FollowerRecyclerViewAdapter(this)
        recyclerView.adapter = recyclerViewAdapter
        val layoutManager = LinearLayoutManager(activity)
        layoutManager.orientation = LinearLayoutManager.VERTICAL
        recyclerView.layoutManager = layoutManager
    }

    inner class FollowerRecyclerViewAdapter
    constructor(
        private val fragment: FollowerFragment
    ) : RecyclerView.Adapter<FollowerRecyclerViewAdapter.FollowerItemViewHolder>() {

        inner class FollowerItemViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {

            private var textViewNickname: TextView = itemView.findViewById(R.id.fragment_follower_item_tv_nickname)
            private var imageViewAvatar: ImageView = itemView.findViewById(R.id.fragment_follower_item_iv_avatar)

            fun bind(user: User) {
                textViewNickname.text = user.nickname
                Glide.with(fragment).load(user.image).into(imageViewAvatar)
            }
        }

        override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): FollowerItemViewHolder {
            val view = LayoutInflater.from(parent.context).inflate(R.layout.fragment_follower_item, parent, false)
            return FollowerItemViewHolder(view)
        }

        override fun getItemCount(): Int = users.size

        override fun onBindViewHolder(holder: FollowerItemViewHolder, position: Int) {
            holder.bind(users[position])
        }
    }
}

data class User
constructor(
    val nickname: String,
    val image: String
)